import React from 'react';
import tim from './images/Tim3.jpeg'
import './About.css';

import { Modal, Button } from 'react-bootstrap';
import AddCustomerModal from './AddCustomerModal';


function MyVerticallyCenteredModal(props) {
    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Ab
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>

            </Modal.Body>

        </Modal>
    );
}

const About = () => {

    const [modalShow, setModalShow] = React.useState(false);



    return (
        <div className='about' id='about'>

            <div className='container'>
                <img src={tim} alt='Tim' />
                <div className='colon'>

                    <h2>About</h2>
                    <span className='line'></span>
                    <p>
                        I have a strong background in business development, startups, business management, service industry, finance, investment and coaching. I love everything related to business, finance, property, stocks, crypto and NFTs.
                    </p>
                    <p>Hi, I am Tim and I am glad you're here. I would love to take you on a financial transformation journey.</p>
                    <button className="button btn-outline-primary" >Explore more</button>

                    <Button className='button btn-outline-warning' variant="secondary " onClick={() => setModalShow(true)}>
                        30-minute FREE
                        initial consultations
                    </Button>
                </div>

                <MyVerticallyCenteredModal
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                />
            </div>
        </div >
    )
}

export default About
