import React from 'react'
import emailjs from 'emailjs-com';

import './Contact.css'

const Contact = () => {


    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm('gmail', 'template_pifi5dn', e.target, 'user_WhaTNUCTK5VoOhcdh0zjl')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
        e.target.reset();
    };


    return (
        <div className='contact'>
            <div className='leftSide'>

            </div>
            <div className='rightSide'>
                <h1>Contact Us</h1>

                <form onClick={sendEmail}>
                    <label htmlFor='name'>Full Name</label>
                    <input name='toname' placeholder='Enter full name...' type='text' />

                    <label htmlFor='name'>Full Name</label>
                    <input name='from_name' placeholder='Enter full name...' type='text' />

                    <label htmlFor='email'>Email</label>
                    <input name='email' placeholder='Enter email...' type='email' required />

                    <label htmlFor='message'>Message</label>
                    <textarea rows='6' name='message' placeholder='Enter message...'>

                    </textarea>
                    <button type='submit'>Send Mesaage</button>
                </form>
            </div>

        </div >
    );
}

export default Contact
