import React from 'react';

import emailjs from "emailjs-com";

import './Contact.css'

const Contact = () => {

    function sendEmail(e) {
        e.preventDefault();

        emailjs.send("service_r8vpj0a", "template_pifi5dn", e.target, "user_WhaTNUCTK5VoOhcdh0zjl").then(res => {
            console.log("Giriş yaptın");
        }, error => {
            console.log('giriş Başarısız', error);
        });

        e.target.reset();
    }

    return (

        <div>

            <div className='my-5'>
                <h1 className='text-center'> Contact Us </h1>
            </div >

            <div className='container contact_div bota'>
                <div className='row'>
                    <div className='col-md-6 col-10 mx-auto'>
                        <form onSubmit={sendEmail}>

                            <div class="mb-3">
                                <label for="name" class="form-label">FullName</label>
                                <input type="text" class="form-control" name="name" placeholder="Enter your name" required />
                            </div>

                            <div class="mb-3">
                                <label for="user_phone" class="form-label">Phone</label>
                                <input type="number" class="form-control" name="user_phone" placeholder="mobile number" required />
                            </div>

                            <div class="mb-3">
                                <label for="user_email" class="form-label">E-mail address</label>
                                <input type="email" class="form-control" name="user_email" placeholder="name@example.com" required />
                            </div>

                            <div class="mb-3">
                                <label for="message" class="form-label">Message</label>
                                <textarea class="form-control" name="message" rows="3" required></textarea>
                            </div>

                            <div class="col-12">
                                <button class=" btn btn-outline-primary" type="submit" value="Sen" > Send</button>
                            </div>

                        </form>
                    </div>
                </div>
            </div >



        </div >
    );
}

export default Contact
